import { z } from 'zod';

export const acaiSchema = z.object({
  sabor: z.enum(['MORANGO', 'BANANA', 'KIWI']).nonempty(),
  tamanho: z.enum(['PEQUENO', 'MEDIO', 'GRANDE']).nonempty(),
  complemento: z.enum(['GRANOLA', 'PAÇOCA', 'LEITE NINHO', 'NENHUM']).nonempty(),
});
